import React from "react";
import Card from 'react-bootstrap/Card'
import Button from 'react-bootstrap/Button'

class BookCard extends React.Component {
    render() {
        return (
            <div>
                <Card style={{ width: '18rem' }}>
                    <Card.Img variant="top" />
                    <Card.Body>
                    <img onChange={(event)=>this.setState({title:event.target.value})} name='url' src='https://cdn.playlists.net/images/playlists/image/medium/136783.jpg' />
                        <Card.Title>{this.props.item.title}</Card.Title>
                        <Card.Text>
                        {this.props.item.description}
                        </Card.Text>
                        <Card.Text>
                        {this.props.item.status}
                        </Card.Text>
                        <Button variant="primary"
                         onClick={() =>this.props.deleteBook(this.props.item._id)}>Delete book</Button>
                    </Card.Body>
                </Card>
            </div>
        )
    }
}
export default BookCard
