import React from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import Jumbotron from 'react-bootstrap/Jumbotron';
import './BestBooks.css';
import { withAuth0 } from '@auth0/auth0-react'
import axios from 'axios'
import BookCard from './BookCard'
import Form from 'react-bootstrap/Form'
import Button from 'react-bootstrap/Button'
import Modal from 'react-bootstrap/Modal'
// import BookItem from './BookItem'

class MyFavoriteBooks extends React.Component {

  constructor(props) {
    super(props)
    this.state = {
      booksArr: []
    }
  }

  componentDidMount = () => {
    const { user } = this.props.auth0
    const email = user.email
    axios
      .get(`http://localhost:3030/getBooks?email=${email}`)
      .then(result => {
        this.setState({
          booksArr: result.data
        })
      })
      .catch(error => {
        console.log('error');
      })
  }
  addBook = (event) => 
  {
    event.preventDefault()
    const { user } = this.props.auth0
    const email = user.email
    const obj ={
      title:event.target.title.value,
      description:event.target.description.value,
      status:event.target.status.value,
      email:email
    }
    axios
    .post(`http://localhost:3030/addBook` ,obj)
    .then(result => {
      this.setState({
        booksArr:result.data
      })
    })
    .catch(error => {
      console.log('Error');
    })
  }

  deleteBook = (id) => 
  {
    const { user } = this.props.auth0
    const email = user.email
    axios
    .delete(`http://localhost:3030/deleteBook/${id}?email=${email}`)
    .then(result => {
      this.setState({
        booksArr:result.data
      })
    })
    .catch(error => {
      console.log('Error');
    })
  }
  render() {
    return (
      <>
      <Button onClick>
        Add
      <Modal>
        <Form onSubmit={this.addBook}>
          <Form.Group className="mb-3" controlId="formBasicEmail">
            <Form.Label>Book Title</Form.Label>
            <Form.Control type="text" name='title'/>
            <Form.Label>Book description</Form.Label>
            <Form.Control type="text" name='description'/>
            <Form.Label>Book status</Form.Label>
            <Form.Control type="text" name='status'/>
          </Form.Group>
          <Button type='submit'>Add a book</Button>
        </Form>
      </Modal>
       </Button>

        {this.state.booksArr.map((item) =>{
          return(
            <BookCard item={item} deleteBook={this.deleteBook}/>
          )
        })}
    </>
    )
  }
}

export default withAuth0(MyFavoriteBooks)
