import React from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import './BestBooks.css';
import { withAuth0 } from '@auth0/auth0-react'
import axios from 'axios'
import BookCard from './BookCard'
import Form from 'react-bootstrap/Form'
import Button from 'react-bootstrap/Button'
import Modal from 'react-bootstrap/Modal'

// import BookItem from './BookItem'

class MyFavoriteBooks extends React.Component {

  constructor(props) {
    super(props)
    this.state = {
      title:'',
      description:'',
      status:'',
      booksArr: []
    }
  }

  componentDidMount = () => {
    const { user } = this.props.auth0
    const email = user.email
    axios
      .get(`http://localhost:3030/getBooks?email=${email}`)
      .then(result => {
        this.setState({
          booksArr: result.data
        })
      })
      .catch(error => {
        console.log('error');
      })
  }
  addBook = (event) => {
    event.preventDefault()
    const { user } = this.props.auth0
    const email = user.email
    const obj = {
      title: this.state.title,
      description: this.state.description,
      status: this.state.status,
      email: email
    }
    axios
      .post(`http://localhost:3030/addBook`, obj)
      .then(result => {
        this.setState({
          booksArr: result.data
        })

      })
      .catch(error => {
        console.log('Error');
      })
  }

  deleteBook = (id) => {
    const { user } = this.props.auth0
    const email = user.email
    axios
      .delete(`http://localhost:3030/deleteBook/${id}?email=${email}`)
      .then(result => {
        this.setState({
          booksArr: result.data
        })
      })
      .catch(error => {
        console.log('Error');
      })
  }

  render() {
    return (
      <>
        <Form onSubmit={this.addBook}>
          <Form.Group className="mb-3" controlId="formBasicEmail">
            <Form.Label>Book Title</Form.Label>
            <Form.Control onChange={(event)=>this.setState({title:event.target.value})} type="text" name='title' />
            <Form.Label>Book description</Form.Label>
            <Form.Control onChange={(event)=>this.setState({description:event.target.value})} type="text" name='description' />
            <Form.Label>Book status</Form.Label>
            <Form.Select name='status' onChange={(event)=>this.setState({status:event.target.value})}>
              <option value='Available'>Available</option>
              <option value='Not Available'>Not Available</option>
            </Form.Select>
          </Form.Group>
          <Button type='submit'>Add a book</Button>
        </Form>


        {this.state.booksArr.map((item) => {
          return (
            <BookCard item={item} deleteBook={this.deleteBook} />
          )
        })}
      </>
    )
  }
}

export default withAuth0(MyFavoriteBooks)
